		$query = "UPDATE wm_pages SET META_Description = '".$value."' WHERE ID = ".$key;
		$db->runQuery($query);
	} else { // If Aliasz
		$query = "UPDATE wm_pages SET META_Description = '".$value."' WHERE Alias LIKE 'www.sheba.co.il/".trim($key)."'";
		$db->runQuery($query);
	}
	echo $query."<br>";
	
	$result = mysqli_error($db->conn);
	//$result = mysqli_affected_rows($db->conn);
	print_r($result)."<br>";

	//mysql_close($db->conn);

	if(intval($result) == 1){
		array_push($successURLS, $key);
		$totalSuccess++;
	} else {
		array_push($failedURLS, $key);
	}

}

